import React from "react";
import ButtonBase from "../buttons/ButtonBase";

interface CardProps {
    children: React.ReactNode;
}

const Card: React.FC<CardProps> = ({ children }) => {
    return (
    <>
      <div className="max-w-sm px-6 py-8 shadow-xl shadow-gray-300 rounded-xl flex flex-col justify-between gap-2">
        <div className="flex justify-between gap-3 mb-2">
          <div className="flex flex-col gap-4">
            <div className="flex items-center w-min pr-2 rounded-xl bg-green-100">
              <div className="w-6 h-5 md:h-6 mr-1 md:mr-1.5 rounded-xl bg-gray-100"></div>
              <p className="text-xs text-gray-600">Active</p>
            </div>
            <h2 className="font-medium text-sm text-gray-700">Per√∫</h2>
            <p className="text-gray-400 font-medium text-xs">3 days plan 5GB</p>
          </div>
          <div className="bg-gray-300 w-14 h-14 md:w-16 md:h-16 ml-2 lg:ml-3 flex flex-col items-center justify-center">
            <span className="text-sm font-medium">0.0</span>
            <span className="text-xs font-medium">/ 5GB</span>
          </div>
        </div>
        <ButtonBase className="w-full rounded-xl px-5 py-3 text-xs font-medium bg-white hover:bg-gray-50 border border-neutral-300 text-gray-500">
          View details
        </ButtonBase>
        <ButtonBase className="w-full rounded-xl px-5 py-3 text-xs font-medium bg-green-300 hover:bg-green-400 text-white">
          Add more data
        </ButtonBase>
      </div>
    </>
  );
};

export default Card;
