import React, { Component } from "react";
import Button from "../Button/button";

interface CardProps {
  children: React.ReactNode;
}

class Card extends Component<CardProps> {  
    render() {
      const { cardData } = this.props;

      const { id, userId, plan, dateStart, dateEnd, status, comsumption, flag, country } = cardData;

      const consumptionValue = comsumption.totalComsumption / 1000000; // Convertir a GB
      const consumptionTotal = plan.match(/(\d+)GB/)[1]; // Extraer el valor de GB del plan

      return (
        <>
          <div className="max-w-sm px-6 py-8 shadow-xl shadow-gray-300 rounded-xl flex flex-col justify-between gap-3">
              <div className="flex justify-between gap-3">
                  <div className="flex flex-col gap-4">
                      <div className="flex items-center w-min pr-2 rounded-xl bg-red-100">
                          <div className="w-6 h-5 md:h-6 mr-1 md:mr-1.5 rounded-xl bg-gray-100"></div>
                          <p className="text-xs text-gray-600">{status}</p>
                      </div>
                      <h2 className="font-medium text-sm text-gray-700">{country}</h2>
                      <p className="text-gray-400 font-medium text-xs mb-1.5">{plan}</p>
                  </div>
                  <div className="bg-gray-300 w-14 h-14 md:w-16 md:h-16 ml-2 lg:ml-3 flex flex-col items-center justify-center">
                    <span className="text-sm font-medium">{consumptionValue.toFixed(1)}</span>
                    <span className="text-xs font-medium">/ {consumptionTotal}GB</span>
                  </div>                  
              </div>
              <Button>
              </Button>
          </div>
        </>
      );
    }
  }
  
  export default Card;

