    plan: string;
    dateStart: string;
    dateEnd: string;
    status: string;
    comsumption: {
      totalComsumption: number;
    };
    flag: string;
    country: string;
  };*/
}

class Card extends Component<CardProps> {  
    render() {
      /*const { cardData } = this.props;

      const { id, userId, plan, dateStart, dateEnd, status, comsumption, flag, country } = cardData;

