import React from "react";
import ButtonBase from "../buttons/ButtonBase";

interface CardProps {
    children: React.ReactNode;
    cardData: {
        id: number;
        userId: number;
        status: string;
        plan: string;
        dateStart: string;
        dateEnd: string;
        comsumption: {
            totalComsumption: number;
        };
        flag: string;
        country: string;
    };
}

const Card: React.FC<CardProps> = ({ children, cardData }) => {
    const {
        id,
        userId,
        status,
        plan,
        dateStart,
        dateEnd,
        comsumption,
        flag,
        country,
      } = cardData;

    const consumptionValue = comsumption.totalComsumption / 1000000; // Convertir a GB
    const consumptionTotal = plan ? plan.match(/(\d+)GB/)[1] : "0";

    return (
        <>
            <div className="max-w-sm px-6 py-8 shadow-xl shadow-gray-300 rounded-xl flex flex-col justify-between gap-2">
                <div className="flex justify-between gap-3 mb-2">
                    <div className="flex flex-col gap-4">
                        <div id="" className="flex items-center w-min pr-2 rounded-xl bg-green-100">
                            <div className="w-6 h-5 md:h-6 mr-1 md:mr-1.5 rounded-xl bg-gray-100"></div>
                            <p className="text-xs text-gray-600">{status}</p>
                        </div>
                        <h2 className="font-medium text-sm text-gray-700">Per√∫</h2>
                        <p className="text-gray-400 font-medium text-xs">3 days plan 5GB</p>
                    </div>
                    <div className="bg-transparent rounded-full border-2 border-green-200 w-14 h-14 md:w-16 md:h-16 ml-2 lg:ml-3 flex flex-col items-center justify-center">
                        <span className="text-sm font-medium">{consumptionValue}</span>
                        <span className="text-xs font-medium">/ {consumptionTotal}GB</span>
                    </div>                  
                </div>
                <ButtonBase className='bg-white hover:bg-gray-50 border border-neutral-300 text-gray-500'>View details</ButtonBase>
                <ButtonBase className='bg-green-300 hover:bg-green-400 text-white'>Add more data</ButtonBase>
            </div>
        </>
      );
};

export default Card;
